CREATE TABLE `donate_unitpay` (
  `id` int(11) NOT NULL,
  `data` datetime NOT NULL,
  `operator` varchar(20) NOT NULL DEFAULT '',
  `paymentType` varchar(20) NOT NULL DEFAULT '',
  `phone` varchar(50) NOT NULL DEFAULT '',
  `out_summ` float(16,2) NOT NULL DEFAULT '0.00',
  `profit` float(16,2) NOT NULL DEFAULT '0.00',
  `unitpayId` int(11) NOT NULL DEFAULT '0',
  `don_kurs` float(16,2) NOT NULL DEFAULT '1',
  `money` int(11) NOT NULL DEFAULT '0',
  `act_bonus` int(11) NOT NULL DEFAULT '0',
  `bonus_money` int(11) NOT NULL DEFAULT '0',
  `login` varchar(25) CHARACTER SET utf8 NOT NULL DEFAULT '',
  `userid` int(11) NOT NULL DEFAULT '0',
  `ip` varchar(15) NOT NULL DEFAULT '',
  `serv` int(11) NOT NULL DEFAULT '1',
  `status` int(11) NOT NULL DEFAULT '0',
  `errMsg` varchar(255) CHARACTER SET utf8 NOT NULL DEFAULT ''
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

ALTER TABLE `donate_unitpay`
  ADD PRIMARY KEY (`id`);

ALTER TABLE `donate_unitpay`
  MODIFY `id` int(11) NOT NULL AUTO_INCREMENT;

ALTER TABLE `donate_unitpay` ADD INDEX(`status`);

ALTER TABLE `donate_unitpay` ADD INDEX(`data`);

CREATE TABLE IF NOT EXISTS `online_stat` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `data` datetime NOT NULL,
  `online_acc` int(11) NOT NULL,
  `online_pers` int(11) NOT NULL,
  `online_world` int(11) NOT NULL,
  `online_instance` int(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=latin1;

CREATE TABLE IF NOT EXISTS `shop_icons` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(50) CHARACTER SET utf8 NOT NULL,
  `icon` blob NOT NULL,
  UNIQUE KEY `name` (`name`),
  KEY `id` (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=latin1;

CREATE TABLE `donate_freekassa` (
  `id` int(11) NOT NULL,
  `data` datetime NOT NULL,
  `currency_id` int(11) NOT NULL DEFAULT '0',
  `email` varchar(45) NOT NULL DEFAULT '',
  `out_summ` float(16,2) NOT NULL,
  `intid` int(11) NOT NULL DEFAULT '0',
  `don_kurs` float(16,2) NOT NULL,
  `money` int(11) NOT NULL,
  `act_bonus` int(11) NOT NULL,
  `bonus_money` int(11) NOT NULL,
  `login` varchar(25) CHARACTER SET utf8 NOT NULL,
  `userid` int(11) NOT NULL,
  `ip` varchar(15) NOT NULL,
  `serv` int(11) NOT NULL DEFAULT '1',
  `status` int(11) NOT NULL DEFAULT '0'
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

ALTER TABLE `donate_freekassa`
  ADD PRIMARY KEY (`id`);

ALTER TABLE `donate_freekassa`
  MODIFY `id` int(11) NOT NULL AUTO_INCREMENT;

ALTER TABLE `donate_freekassa` ADD INDEX(`status`);

ALTER TABLE `donate_freekassa` ADD INDEX(`data`);

CREATE TABLE `config` (
  `id` int(11) NOT NULL,
  `name` varchar(255) NOT NULL,
  `type` varchar(20) NOT NULL DEFAULT 'int',
  `value` text CHARACTER SET utf8 NOT NULL,
  `section` int(11) NOT NULL DEFAULT '0',
  `group` int(11) NOT NULL DEFAULT '0',
  `desc` text CHARACTER SET utf8 NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

ALTER TABLE `config`
  ADD PRIMARY KEY (`id`);

ALTER TABLE `config`
  MODIFY `id` int(11) NOT NULL AUTO_INCREMENT;


CREATE TABLE IF NOT EXISTS `skill_icons` (`id` int(11) NOT NULL AUTO_INCREMENT, `name` varchar(50) CHARACTER SET utf8 NOT NULL, `icon` blob NOT NULL, UNIQUE KEY `name` (`name`),  KEY `id` (`id`)) ENGINE=MyISAM  DEFAULT CHARSET=latin1;
UPDATE `config` SET `group`=2 WHERE `name`='freekassa_merchant_id' OR `name`='freekassa_secret_word1' OR `name`='freekassa_secret_word2' OR `value`='Параметры сервиса Free-Kassa';
